int n;
double x[1010], y[1010];

double EPS = 1e-10;

//誤差を考慮して足し算を行う
double add(double a, double b) {
	if (abs(a + b) < EPS*(abs(a) + abs(b)))return 0;
	return a + b;
}

//二次元ベクトル構造体

struct P {
	double x, y;
	P() {}
	P(double x, double y) :x(x), y(y) {
	}
	P operator + (P p) {
		return P(add(x, p.x), add(y, p.y));
	}
	P operator - (P p) {
		return P(add(x, -p.x), add(y, -p.y));
	}
	P operator * (double d) {
		return P(x*d, y*d);
	}
	//内積
	double dot(P p) {
		return add(x*p.x, y*p.y);
	}
	//外積
	double det(P p) {
		return add(x*p.y, -y*p.x);
	}
};


//p1-p2じょうにqがあるか
bool on_seg(P p1, P p2, P q) {
	return (p1 - q).det(p2 - q) == 0 && (p1 - q).dot(p2 - q) <= 0;
}

P ps[1010];

int main() {
	cin >> n;
	REP(i, n) {
		int x, y;
		cin >> x >> y;
		ps[i].x = x;
		ps[i].y = y;
	}
	for (int i = 1; i < n; i++) {
		bool boo[1010] = {};
		boo[0] = boo[i] = true;
		REP(j, n) {
			if (j == 0 || j == i)continue;
			if (on_seg(ps[0], ps[i], ps[j])|| on_seg(ps[0], ps[j], ps[i])|| on_seg(ps[i], ps[j], ps[0]))boo[j] = true;
		}
		vector<P>other;
		REP(j, n) {
			if (!boo[j])other.emplace_back(ps[j]);
		}
		if (other.size() <= 2 && other.size() != 0) {
			cout << "Yes" << endl;
			return 0;
		}
		bool isok = true;
		for (int j = 2; j < other.size(); j++) {
			if (!(on_seg(other[0], other[1], other[j]) || on_seg(other[0], other[j], other[1]) || on_seg(other[1], other[j], other[0])))isok = false;
		}
		if (isok&&other.size()!=0) {
			cout << "Yes" << endl;
			return 0;
		}
	}
	cout << "No" << endl;
}
