#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	string k, s, tmp[1000];
	bool flag[1000];
	for (int i = 0; i < 1000; i++) flag[i] = false;
	getline(cin, k);
	getline(cin, s);
	int count = 0;
	for (int i = 0; i < k.size(); i++) {
		if (k[i] != ' ') tmp[count].push_back(k[i]);
		else count++;
	}

	for (int i = 0; i < count + 1; i++) {
		int a = s.find(tmp[i]);
		if (a != std::string::npos) cout << tmp[i] << endl;
	}

	return 0;
}
