#include <iostream>
#include <string>
void fanc(char ch, int& in) {
	if (ch == '0')
		in *= 10;
	if (ch == '1')
		in = in * 10 + 1;
	if (ch == '2')
		in = in * 10 + 2;
	if (ch == '3')
		in = in * 10 + 3;
	if (ch == '4')
		in = in * 10 + 4;
	if (ch == '5')
		in = in * 10 + 5;
	if (ch == '6')
		in = in * 10 + 6;
	if (ch == '7')
		in = in * 10 + 7;
	if (ch == '8')
		in = in * 10 + 8;
	if (ch == '9')
		in = in * 10 + 9;
}
int main() {
	int ji = 0, hun = 0, ji2 = 0, hun2 = 0;
	int a = 0;
	int count, count2;
	std::string str, str2;
	std::cin >> str >> str2;
	for (int i = 0; i < str.size(); ++i) {
		if (a == 0)
			fanc(str[i], ji);
		else
			fanc(str[i], hun);
		if (str[i] == ':')
			a = 1;
		
	}
	a = 0;
	for (int i = 0; i < str2.size(); ++i) {
		if (a == 0)
			fanc(str2[i], ji2);
		else
			fanc(str2[i], hun2);
		if (str2[i] == ':')
			a = 1;
	}
	if (ji > ji2)
		count = 24 - (ji - ji2);
	else if (ji < ji2)
		count = ji2 - ji;
	if (hun > hun2)
		count2 = 60 - (hun - hun2);
	else if (hun < hun2)
		count2 = hun2 - hun;

	std::cout << count + count2 << std::endl;
	system("pause");
	return 0;
}
