#include <iostream>
#include <string>
#include<cstdlib>
#include<algorithm>
#include<string>
#include<vector>
using namespace std;
string now, hosii;
int nowtoki = 0;
int nowhun = 0;
int hosiitoki = 0;
int hosiihun = 0;
void makai() {
	if (hosii[0] - '0' == 0) {
		hosiitoki = 24 + (hosii[1] - '0');
	}
	else {
		hosiitoki = hosii[1] - '0';
	}
}

void mazai() {
	if (now[0] - '0' == 0) {
		nowtoki = 24 + (now[1] - '0');
	}
	else {
		nowtoki = (now[1] - '0');
	}
}

int main() {
	cin >> now >> hosii;
	hosiihun = (hosii[3]-'0') * 10 + (hosii[4] - '0');
	makai();
	mazai();
	cout << hosiitoki << hosiihun << '\n';
	nowhun = (now[3] - '0') * 10 + (now[4] - '0');
	if (hosiihun - nowhun < 0) {
		cout << hosiitoki - nowtoki + hosiihun << '\n';
	}
	else {
		cout << hosiitoki - nowtoki + hosiihun - nowhun << '\n';
	}
}
