	//std::cout << (n + m) + (n - m) + (n*m) + (n / m) + (n%m) << std::endl;
	temp += n + m;
	if (n - m < 0) {
		temp += m - n;
	}
	else {
		temp += n - m;
	}
	temp += n * m;
	if (n / m < 1) {
