#include<iostream>
#include<cmath>
#include<algorithm>
#include <cassert>
#include <stdio.h>
#include<string>
using namespace std;
int main() {

	int i = 0,k=0,count=0;
	cin >> i;
	k = i;
	for (int j = 0; j < k; j++) {
		if (i != 1) {
			if (i % 2 == 1) {
				i=i * 3 + 1;
			}
			else {
				i=i / 2;
			}

			count++;
		}
	}
	cout << count << endl;
	return 0;
}
