#include<iostream>
#include<cmath>
#include<vector>
#include<algorithm>

#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

int main() {
	ll n = 0;
	ll ans = 0;
	std::cin >> n;
	ll i = 0, j = 0;
	std::vector<ll> prime;
	prime.push_back(0);
	rep(i, n) {
		prime.push_back(1);
	}
	for (int i = 1; i < std::sqrt(n); i++) {

		if (prime[i] == 1) {
			for (j = i + 1; j*(i + 1) <= n; j++) {

				prime[(i + 1)*j - 1] = 0;


			}

		}


	}
	rep(i, n) {
		if (prime[i] == 1) {
			ans++;
		}
	}
	std::cout << ans << std::endl;

}
