#include <iostream>
	int main() {
		int N, A, B, C, D;
		std::cin >> N>> A >> B >> C >> D;
		if (N%A == 0) {
			if (N%C == 0) {
				if (N/ A * B <= N / C * D) {
					std::cout << N / A * B << std::endl;
				}
				else {
					std::cout << N / C * D << std::endl;
				}
			}
		}
		else {
			if (N%C == 0) {
				std::cout << N / C * D << std::endl;
			}
			else if (N / C * D <= N / A * B) {
			std::cout << N / C* D << std::endl;
			}
			else {
				std::cout << N / A * B << std::endl;
			}
		}
		std::cout << std::endl;
		return 0;
	}
