LL dp[20000][2][2][3];
LL func(string x) {
	rep(i, x.size() + 1)rep(j, 2)rep(k, 2)rep(l, 3)
		dp[i][j][k][l] = 0;
	dp[0][0][0][0] = 1;
	rep(i, x.size()) {
		rep(j, 2) {
			int lim = j ? 9 : x[i] - '0';
			rep(k, lim + 1)rep(l, 2)rep(n, 3) {
				dp[i+1][j || k < lim][l || k == 3][(n + k) % 3] += dp[i][j][l][n];
				dp[i+1][j || k < lim][l || k == 3][(n + k) % 3] %= MOD;
			}
		}
	}
	LL ret = 0;
	rep(i, 2)rep(j, 3) rep(k, 2){
		if(j==0||k==1)
		ret += dp[x.size()][i][k][j];
		ret %= MOD;
	}
	return ret;
}
