#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int main() {
	std::string str, str1;
	std::cin >> str >> str1;
	int nowhour = 0, nowmin = 0, thour = 0, tmin = 0;
	nowhour += (str[0] - '0') * 10;
	nowhour += (str[1] - '0') * 1;
	nowmin += (str[3] - '0') * 10;
	nowmin += (str[4] - '0') * 1;
	thour += (str1[0] - '0') * 10;
	thour += (str1[1] - '0') * 1;
	tmin += (str1[3] - '0') * 10;
	tmin += (str1[4] - '0') * 1;
	int off0 = 1, no = 0;
	off0 += thour;
	off0 += tmin;
	if (thour >= nowhour)no += thour - nowhour;
	else no += (24 - nowhour) + thour;
	if (tmin >= nowmin)no += tmin - nowmin;
	else no += (60 - nowmin) + tmin;
	if (off0 > no)std::cout << no << std::endl;
	else std::cout << off0 << std::endl;
	return 0;
}
