int main() {
	int a;
	std::cin >> a;
	int ans = 0;
	while (a != 1) {
		if (a % 2 == 0) {
			a /= 2;
		}
		else {
			a *= 3;
			a += 1;
		}
		ans++;
	}
	std::cout << ans << std::endl;
