#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int main() {
	int a;
	std::cin >> a;
	int count = 0;
	while (a != 1) {
		if (a % 2 == 0) {
			a /= 2;
		}
		else {
			a = a * 3 + 1;
		};
		count++;
	}
	std::cout << count << std::endl;
	return 0;
}
