#include <iostream>
#include <vector>
#include <string>
using namespace std;

int a[100000];


int main() {
	int n;
	cin >> n;

	int b = 0;

	for (int i = 0; i < n; i++) {
		a[i] = 1;
	}
	for (int i = 2; i < sqrt(n); i++) {
		if (a[i]) {

			for (int j = 0; i * (j + 2) < n; j++) {
				a[i *(j + 2)] = 0;
			}

		}
	}

	for (int i = 2; i < n; i++) {
		if (a[i]) {

			b++;
		}
	}
	cout << b << endl;
}
