int main() {
	int a[100100];
	int n;
	ll m;
	cin >> n >> m;
	fill(a, a + n, 1);
	a[n] = n;
	for (ll i = 0; i < m; i++) {
		if (a[n + i] * 2 - a[i] >= 0) {
			a[n + i + 1] = (a[n + i] * 2 - a[i]) % susu;
		}
		else {
			a[n + i + 1] = susu - (a[n + i] * 2 - a[i]);
		}
	}
	cout << a[m - 1] << endl;
	return 0;
}
