#include <iostream>
#include <string>
#include<cstdlib>
#include<algorithm>
#include<string>
#include<vector>
using namespace std;

int main() {
	unsigned long long int N = 0;
	int answer = 0;
	cin >> N;
	while (N != 1) {
		if (N % 2 == 0) {
			N = N / 2;
			answer++;
		}
		else {
			N = 3 * N + 1;
			answer++;
		}
	}
	cout << answer << '\n';
	return 0;
}
