#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
#include <stdlib.h>

int main()
{
	int n, total=0;
	bool notprime[1000000]={};
	std::cin >> n;
	for(int i = 2; i * i <= n; i++){
		if(notprime[i]) continue;
		for(int j = 2; j <= n / i; j++){
			notprime[j * i] = true;
		}
	}
	for(int i = 2; i <= n; i++){
		if(!notprime[i]) total++;
	}
	std::cout << total << std::endl;
	return 0;
}
