#include <iostream>

constexpr int MAX_NM = 100000;
constexpr long MOD = 1000000007;

int main(){

	long fib[MAX_NM] = {1, 1};

	for(int i = 2; i < MAX_NM; ++i){
		fib[i] = fib[i - 1] % MOD + fib[i - 2] % MOD;
		fib[i] %= MOD;
	}

	int T;

	std::cin >> T;

	while(T--){

		int N, M;
		long A[MAX_NM], B[MAX_NM];

		std::cin >> M >> N;

		int result = 0;

		for(int i = 0; i < M; ++i){
			std::cin >> A[i];
			result += (A[i] % MOD) * (fib[N - 3] % MOD) * N % MOD;
		}
		for(int i = 0; i < M; ++i){
			std::cin >> B[i];
			result += (B[i] % MOD) * (fib[N - 2] % MOD) * N % MOD;
		}

		std::cout << result << std::endl;
	}

	return 0;
}
