#include<iostream>
int main() {
	int n;
	std::cin >> n;
	static int a[1000002] = {};
	for (int i = 2; i*i <= n; i++) {
		if (a[i] == 0) {
			for (int j = i + 2; j <= n; j++) {
				if (j % i == 0) {
					a[j] = 1;
				}
			}
		}
	}
	int ans = 0;
	for (int i = 2; i <= n; i++) {
		if (a[i] == 0) {
			ans++;
		}
	}
	std::cout << ans << std::endl;
}
