# include "bits/stdc++.h"
using namespace std;
using LL = long long;
using ULL = unsigned long long;
const double PI = acos(-1);
template<class T>constexpr T INF() { return ::std::numeric_limits<T>::max(); }
template<class T>constexpr T HINF() { return INF<T>() / 2; }
template <typename T_char>T_char TL(T_char cX) { return tolower(cX); };
template <typename T_char>T_char TU(T_char cX) { return toupper(cX); };
typedef pair<LL, LL> pii;
const int vy[] = { -1, -1, -1, 0, 1, 1, 1, 0 }, vx[] = { -1, 0, 1, 1, 1, 0, -1, -1 };
const int dx[4] = { 0,1,0,-1 }, dy[4] = { 1,0,-1,0 };
int popcnt(unsigned long long n) { int cnt = 0; for (int i = 0; i < 64; i++)if ((n >> i) & 1)cnt++; return cnt; }
int d_sum(LL n) { int ret = 0; while (n > 0) { ret += n % 10; n /= 10; }return ret; }
int d_cnt(LL n) { int ret = 0; while (n > 0) { ret++; n /= 10; }return ret; }
LL gcd(LL a, LL b) { if (b == 0)return a; return gcd(b, a%b); };
LL lcm(LL a, LL b) { LL g = gcd(a, b); return a / g*b; };
# define ALL(qpqpq)           (qpqpq).begin(),(qpqpq).end()
# define UNIQUE(wpwpw)        sort(ALL((wpwpw)));(wpwpw).erase(unique(ALL((wpwpw))),(wpwpw).end())
# define LOWER(epepe)         transform(ALL((epepe)),(epepe).begin(),TL<char>)
# define UPPER(rprpr)         transform(ALL((rprpr)),(rprpr).begin(),TU<char>)
# define FOR(i,tptpt,ypypy)   for(LL i=(tptpt);i<(ypypy);i++)
# define REP(i,upupu)         FOR(i,0,upupu)
# define INIT                 std::ios::sync_with_stdio(false);std::cin.tie(0)

int V, E;
struct edge { pair<int,int>to; LL cost; };

typedef pair<LL, pair<int,int>> P;
int s;
LL d[303030][5]; //0:乗り換え 1:A,2:B,3:C,4:D
vector<edge> vec[100100][5];

int A,B,C,D;

LL dijkstra(int bit) {
	for (int i = 0; i < 303030; i++)REP(j,5)d[i][j] = HINF<LL>();
	priority_queue<P, std::vector<P>, std::greater<P>> pq;
	d[s][0] = 0;
	pq.push(make_pair(d[s][0], make_pair(s,0)));//cost,to
	while (!pq.empty()) {
		P p = pq.top();
		pq.pop();
		int i = p.second.first;
		int j = p.second.second;
		if (d[i][j] < p.first)continue;
		for (int k = 0; k < vec[i][j].size(); k++) {
			edge e = vec[i][j][k];
			if (e.to.second == 1)if ((bit & 1 << 0) == 0)continue;
			if (e.to.second == 2)if ((bit & 1 << 1) == 0)continue;
			if (e.to.second == 3)if ((bit & 1 << 2) == 0)continue;
			if (e.to.second == 4)if ((bit & 1 << 3) == 0)continue;
			if (d[e.to.first][e.to.second] <= d[i][j] + e.cost)continue;
			d[e.to.first][e.to.second] = d[i][j] + e.cost;
			pq.push(make_pair(d[e.to.first][e.to.second], e.to));
		}
	}
	return d[V - 1][0];
}

int main() {
	cin >> V >> E;
	cin >> A >> B >> C >> D;
	REP(i, E) {
		int p, q;
		char x;
		int l;
		cin >> p >> q >> x >> l;
		p--, q--;
		x -= 'A';
		x++;
		vec[p][x].emplace_back(edge{ make_pair(q,0),l });
		vec[q][x].emplace_back(edge{ make_pair(p,0),l });
	}
	REP(i, V) {
		REP(j, 5) {
			vec[i][0].emplace_back(edge{ make_pair(i,j),0 });
		}
	}
	s = 0;
	LL ans = INF<LL>();
	REP(i, (1 << 4)) {
		LL cur = 0;
		if (i & 1 << 0)cur += A;
		if (i & 1 << 1)cur += B;
		if (i & 1 << 2)cur += C;
		if (i & 1 << 3)cur += D;
		cur += dijkstra(i);
		ans = min(ans, cur);
	}
	cout << ans << endl;
}
