#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

struct edge { ll to, cost, com; };

ll n, m, a[4], p, q, l, ans = INF2;
char x;
ll d[100010];
vector<edge>v[100010];


void dijk(ll start, vector<bool>f) {
	for (int i = 1;i <= n;i++) {
		d[i] = INF2;
	}
	d[1] = start;
	priority_queue < P, vector<P>, greater<P>>pq;
	pq.push(P(0, 1));
	while (!pq.empty()) {
		P pp = pq.top();
		pq.pop();
		ll i = pp.sc, co = pp.fr;
		if (d[i] < co)continue;
		for (auto u : v[i]) {
			if (!f[u.com])continue;
			if (d[u.to] <= d[i] + u.cost)continue;
			d[u.to] = d[i] + u.cost;
			pq.push(P(d[u.to], u.to));
		}
	}
	return;
}


int main() {
	cin >> n >> m;
	cin >> a[0] >> a[1] >> a[2] >> a[3];
	for (int i = 0;i < m;i++) {
		cin >> p >> q >> x >> l;
		v[p].pb(edge{ q,l,x - 'A' });
		v[q].pb(edge{ p,l,x - 'A' });
	}
	for (int i = 0;i < (1 << 4);i++) {
		vector<bool>fl(4);
		ll ct = 0;
		for (int j = 0;j < 4;j++) {
			if (i&(1 << j))fl[j] = 1, ct += a[j];
			else fl[j] = 0;
		}
		dijk(ct,fl);
		ans = min(ans, d[n]);
	}
	cout << ans << endl;
	return 0;
}
