#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int Eratosthenes(int N) {
	int arr[10000];
	for (int i = 0; i < N; i++) {
		arr[i] = 1;
	}
	for (int i = 2; i < sqrt(N); i++) {
		if (arr[i]) {
			for (int j = 0; i * (j + 2) < N; j++) {
				arr[i *(j + 2)] = 0;
			}
		}
	}

	int count = 0;
	for (int i = 2; i < N; i++) {
		if (arr[i]) {
			count++;
		}
	}
	return count;
}

int main() {
	int N;
	std::cin >> N;
	std::cout << Eratosthenes(N) << std::endl;
	return 0;
}
