void visit(int a, vector<LL>& path) {
	while (vec[a].size()) {
		int b = vec[a].back();
		vec[a].erase(vec[a].end()-1);
		visit(b, path);
	}
	path.push_back(a);
}
bool eulerPath(int s) {
	int n = 26, m = 0;
	vector<int> deg(n);
	rep(u, n) {
		m += vec[u].size();
		rep(e, vec[u].size()) --deg[vec[u][e]]; //  in-deg
		deg[u] += vec[u].size();      // out-deg
	}
	LL cc = 0;
	rep(i, 26)if (deg[i] == 0)cc++;
	int k = n - cc;
	if (k == 0) {
		vector<LL>path;
		path.clear();
		visit(s, path);
		return path.size() == m + 1;
	}
	return false;
}
