#include <iostream>

int main() {
	int n, count = 0;
	bool _while = false;
	std::cin >> n;
	while (_while == false) {

		if (n % 2 == 0)
			n /= 2;
		else
			n = n * 3 + 1;

		count += 1;

		if (n == 1)
			_while = true;

	}
	std::cout << count << std::endl;
	system("pause");
	return 0;
}
