#include <iostream>
#include <string>
#include <algorithm>
#define rep(i,n) for(int i=0;i<n;i++)

using ll = long long;
using namespace std;

ll n, m, a, b, ans;
string s, t;
int main() {
	cin >> s >> t;
	n = s[1] - '0'; a = t[1] - '0';
	if (s[0] != '0') n += (s[0] - '0') * 10;
	if (t[0] != '0') a += (t[0] - '0') * 10; //n:m a:b

	m = s[4] - '0'; b = t[4] - '0';
	if (s[3] != '0') m += (s[3] - '0') * 10;
	if (t[3] != '0') b += (t[3] - '0') * 10;
	
	if (n > a) {
		ans += 24 + a - n;
		if (m > b) ans += 60 + b - m;
		else ans += b - m;
	}
	else {
		ans += a - n;
		if (m > b) ans += 60 + b - m;
		else ans += b - m;
	}
	ans = min(ans, a + b + 1);
	cout << ans << endl;
	return 0;
}
