using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
typedef pair<string, string> sP;
typedef pair<ll, pair<ll, ll>> PP;

const ll mod = 1e4;
const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };
#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll dp[10010][2][2][3];//i:桁　j:N未満か否か(yes 0:no 1)　k:3がついているか(yes 1:no 0)　l:mod 3


int main() {
	ll n;
	string s;
	cin >> s;
	n = s.size();
	bool fl = 1;
	for (int i = s.size() - 1;i >= 0;i--) {
		if (i == s.size() - 1 && s[i] == '9') {
			fl = 1;
			s[i] = '0';
		}
		else if (s[i] != '9' && !fl) {
			s[i] += 1;
			break;
		}
		else if (s[i] == '9'&&fl) {
			s[i] = '0';
		}
		else if (s[i] != '9'&&fl) {
			s[i] += 1;
			break;
		}
	}
	//cout << s << endl;
	for (int i = 0;i <= s[0] - '0';i++) {
		if (i == s[0] - '0') {
			if (i == 3)dp[0][1][1][0]++;
			else dp[0][1][0][i % 3]++;
		}
		else {
			if (i == 3)dp[0][0][1][0]++;
			else dp[0][0][0][i % 3]++;
		}
	}
	for (int i = 1;i < n;i++) {
		for (int j = 0;j < 10;j++) {//数字決める
			for (int l = 0;l < 3;l++) {//mod 3の値
				if (j < s[i] - '0') {
					if (j == 3) {
						dp[i][0][1][l] += dp[i - 1][0][1][l] + dp[i - 1][0][0][l];//未満かつ3付く
						dp[i][0][1][l] += dp[i - 1][1][1][l] + dp[i - 1][1][0][l];
					}
					else {
						dp[i][0][0][(l + j) % 3] += dp[i - 1][0][0][l] + dp[i - 1][1][0][l];//未満かつ3付かない
						dp[i][0][1][(l + j) % 3] += dp[i - 1][0][1][l] + dp[i - 1][1][1][l];
					}
					dp[i][0][1][l] %= MOD;
					dp[i][0][0][(l + j) % 3] %= MOD;
					dp[i][0][1][(l + j) % 3] %= MOD;
				}
				else if (j == s[i] - '0') {
					if (j == 3) {
						dp[i][0][1][l] += dp[i - 1][0][1][l] + dp[i - 1][0][0][l];
						dp[i][1][1][l] += dp[i - 1][1][0][l] + dp[i - 1][1][1][l];
					}
					else {
						dp[i][0][0][(l + j) % 3] += dp[i - 1][0][0][l];
						dp[i][0][1][(l + j) % 3] += dp[i - 1][0][1][l];
						dp[i][1][0][(l + j) % 3] += dp[i - 1][1][0][l];
						dp[i][1][1][(l + j) % 3] += dp[i - 1][1][1][l];
					}
					dp[i][0][1][l] %= MOD;
					dp[i][1][1][l] %= MOD;
					dp[i][0][0][(l + j) % 3] %= MOD;
					dp[i][0][1][(l + j) % 3] %= MOD;
					dp[i][1][0][(l + j) % 3] %= MOD;
					dp[i][1][1][(l + j) % 3] %= MOD;
				}
				else {
					if (j == 3) {
						dp[i][0][1][l] += dp[i - 1][0][1][l] + dp[i - 1][0][0][l];
					}
					else {
						dp[i][0][1][(l + j) % 3] += dp[i - 1][0][1][l];
						dp[i][0][0][(l + j) % 3] += dp[i - 1][0][0][l];
					}
					dp[i][0][1][l] %= MOD;
					dp[i][0][1][(l + j) % 3] %= MOD;
					dp[i][0][0][(l + j) % 3] %= MOD;
				}
			}
		}
	}
	cout << (dp[n - 1][0][1][1] + dp[n - 1][0][1][2] + dp[n - 1][0][0][0] + dp[n - 1][0][1][0] - 1) % MOD << endl;
	             //3有り余り1            3有り余り2               3無し余り0　　　　　3有り余り0
	//cout << dp[n - 1][0][1][1] << ' ' << dp[n - 1][0][1][2] << ' ' << dp[n - 1][0][0][0] << ' ' << dp[n - 1][0][1][0] << endl;
	return 0;

}
