#include <iostream>
//using namespace std;

int main() {
	int a, b, c, d, e, f, g, h;
	std::cin >> a >> b;
	c = a + b;
	g = a % b;
	if (a > b) {
		d = a - b;
		e = (a - g) / b;
	}
	else if (a <= b) {
		d = b - a;
		e = (a - g) / b;
	}
	f = a * b;
	h = c + d + e + f + g;
	std::cout << h << std::endl;
}
