const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };
#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll v, e, x, y;

ll par[200010], Rank[200010];


void init(ll n) {
	for (ll i = 0;i <= n;i++)par[i] = i, Rank[i] = 0;
}
ll find(ll x) {
	if (par[x] == x)return x;
	else return par[x] = find(par[x]);
}
void unite(ll x, ll y) {
	x = find(x);
	y = find(y);
	if (x == y)return;
	if (Rank[x] < Rank[y])par[x] = y;
	else {
		par[y] = x;
		if (Rank[x] == Rank[y])Rank[x]++;
	}
}
bool same(ll x, ll y) {
	return find(x) == find(y);
}

int main() {
	cin >> v >> e;
	if (e != v - 1) {
		cout << "No" << endl;
		return 0;
	}
	init(v);
	for (int i = 0;i < e;i++) {
		cin >> x >> y;
		unite(x, y);
	}
	ll root = find(1);
	for (int i = 2;i <= v;i++) {
		if (find(i) != root) {
			cout << "No" << endl;
			return 0;
		}
	}
	cout << "Yes" << endl;
	return 0;
}
