struct matrix {
	int n;
	vector<vector<LL>> v;
	matrix(int n_):n(n_){
		v.resize(n);
		rep(i, n)v[i].resize(n);
		rep(i, n)
			rep(j, n)v[i][j] = 0;
	}
};
matrix mult(matrix a, matrix b) {
	matrix ret(a.n);
	rep(i, a.n) {
		rep(k, b.n) {
			rep(j, b.n) {
				ret.v[i][j] += (a.v[i][k] * b.v[k][j]) % MOD;
			}
		}
	}
	return ret;
}
matrix Pow(matrix a,LL n) {
	matrix b(a.n);
	rep(i, a.n) {
		b.v[i][i] = 1;
	}
	while (n>0) {
		if (n & 1)b = mult(a,b);
		a = mult(a, a);
		n >>= 1;
	}
	return b;
}
