#include<iostream>
int main() {
	int timea, mina, timeb, minb;
	char a, b;
	std::cin >> timea >> a >> mina >> timeb >> b >> minb;
	if (timea <= timeb&&mina <= minb) {
		std::cout << timeb - timea + minb - mina << std::endl;
	}
	else if (timeb < timea&&minb < mina) {
		std::cout << timeb + minb + 1 << std::endl;
	}
	else if (timeb < timea&&minb > mina) {
		if (24 - timea + timeb + minb - mina > 1 + minb + timeb) {
			std::cout << 1 + minb + timeb << std::endl;
		}
		else {
			std::cout << 24 - timea + timeb + minb - mina << std::endl;
		}
	}
	else {
		if (60 - mina + minb + timeb - timea > 1 + minb + timeb) {
			std::cout << 1 + minb + timeb << std::endl;
		}
		else {
			std::cout << 60 - mina + minb + timeb - timea << std::endl;
		}
	}
}
