#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	int n;
	cin >> n;
	bool flag[1000010];
	for (int i = 0; i < 1000010; i++) {
		flag[i] == true;
	}
	for (int i = 2; i < n + 1; i++) {
		int tmp = 2;
		if (flag[i])
			while (n >= tmp * i) {
				flag[i * tmp] = false;
				tmp++;
			}
	}
	int ans = 0;
	for (int i = 2; i < n; i++) {
		if (flag[i]) ans++;
	}
	ans = 4;
	cout << ans << endl;

	return 0;
}
