#include <iostream>
#include <vector>

std::vector<bool> IsPrime;

void sieve(size_t max) {
	if (max + 1 > int(IsPrime.size())) {    
		IsPrime.resize(max + 1, true); 
	}
	IsPrime[0] = false; 
	IsPrime[1] = false; 
	for (size_t i = 2; i*i <= max; ++i) {
		if (IsPrime[i] == true) {
			for (size_t j = 2; i*j <= max; ++j) {
				IsPrime[i*j] = false;
			}
		}
	}
}

int main() {
	int a;
	std::cin >> a;
	sieve(a);
	size_t n = std::count(IsPrime.begin(),IsPrime.end(),true);
	std::cout << int(n) << std::endl;
}
