#include<iostream>
#include<vector>
#include<string>
#include<algorithm>


int main() {
	std::string time_a, time_b;
	std::string henkan_a, henkan_b;
	int zikana = 0, funa = 0, zikanb = 0, funb = 0;
	int ans = 0, func_gutyoku = 0,func_reset = 0;
	int resetans = 0, gutyokuans = 0;
	std::cin >> time_a >> time_b;
	time_a.erase(time_a.begin() + 2);
	time_b.erase(time_b.begin() + 2);
	zikana = std::stoi(time_a.substr(0, 2));
	zikanb = std::stoi(time_b.substr(0, 2));
	funa = std::stoi(time_a.substr(2, 2));
	funb = std::stoi(time_b.substr(2, 2));
	// 時間　愚直
	while (zikana != zikanb) {
		func_gutyoku++;
		zikana++;
		if (zikana == 24) {
			zikana = 0;
		}
	}

	gutyokuans += func_gutyoku;
	func_gutyoku = 0;
	// 分　愚直
	while (funa != funb) {
		funa++;
		if (funa == 60) {
			funa = 0;
		}
		func_gutyoku++;
	}
	gutyokuans += func_gutyoku;

	//リセット
	zikana = 0;
	while (zikana != zikanb) {
		zikana++;
		if (zikana == 24) {
			zikana = 0;
		}
		func_reset++;
	}
	func_reset++;
	func_reset += funb;
	resetans = func_reset;
	ans = std::min(resetans, gutyokuans);
	std::cout << ans << std::endl;
	return 0;
}
