#include <iostream>
#include <vector>

int sieve(int max, std::vector<bool>IsPrime) {
	if (max + 1 > int(IsPrime.size())) {    
		IsPrime.resize(max + 1, true); 
	}
	IsPrime[0] = false; 
	IsPrime[1] = false; 
	for (int i = 2; i*i <= max; ++i) {
		if (IsPrime[i] == true) {
			for (int j = 2; i*j <= max; ++j) {
				IsPrime[i*j] = false;
			}
		}
	}
	int n = std::count(IsPrime.begin(), IsPrime.end(), true);
	return n;
}
