//誤差を考慮して足し算
double add(double a, double b) {
	if (abs(a + b) < eps*(abs(a) + abs(b))) {
		return 0;
	}
	return a + b;
}

//二次元ベクトル構造体
struct pp {
	double x, y;
	pp() {}
	pp(double x, double y) :x(x), y(y) {}
	pp operator+(pp p) {
		return pp(add(x, p.x), add(y, p.y));
	}
	pp operator-(pp p) {
		return pp(add(x, -p.x), add(y, -p.y));
	}
	pp operator*(double d) {
		return pp(add(x, d), add(y, d));
	}
	double dot(pp p) {//内積
		return add(x*p.x, y*p.y);
	}
	double det(pp p) {//外積
		return add(x*p.y, -y * p.x);
	}
};

double dist(pp p, pp q) {//距離の二乗
	return (p - q).dot(p - q);
}

bool cmp_x(const pp& p, const pp& q) {
	if (p.x != q.x)return p.x < q.x;
	return p.y < q.y;
}


//凸包
vector<pp>convex_full(pp* ps, int N) {
	sort(ps, ps + N, cmp_x);
	int k = 0;
	vector<pp>qs(N * 2);
	//下側凸包
	for (int i = 0;i < N;i++) {
		while (k > 1 && (qs[k - 1] - qs[k - 2]).det(ps[i] - qs[k - 1]) <= 0)k--;
		qs[k++] = ps[i];
	}
	//上側凸包
	for (int i = N - 2, t = k;i >= 0;i--) {
		while (k > t && (qs[k - 1] - qs[k - 2]).det(ps[i] - qs[k - 1]) <= 0)k--;
		qs[k++] = ps[i];
	}
	qs.resize(k - 1);
	return qs;
}

double Cosine_theorem(pp a, pp b, pp c) {//余弦定理　角Bが求めたい角度
	double ba = dist(a, b);
	double bc = dist(b, c);
	double ac = dist(a, c);
	double cos_sita = (ba + bc - ac) / (2.0 * sqrt(ba)*sqrt(bc));
	return cos_sita;
}

double get_vector_length(pp v) {//ベクトルのスカラー値
	return pow((v.x * v.x) + (v.y * v.y), 0.5);
}

double Angle(pp A, pp B, pp C)//内積から角度　角Bを求めたい
{
	double length_A = get_vector_length(B - A);
	double length_B = get_vector_length(B - C);

	double cos_sita = (B - A).dot(B - C) / (length_A * length_B);

	if (abs(cos_sita) > 1)return INF;
	else return cos_sita;
	double sita = acos(cos_sita);
}

double xx, yy, zz, ans = INF3;

void get_line(pp a, pp b) {//直線の式の係数を求める
	xx = (b.y - a.y) / (b.x - a.x);
	yy = -1;
	zz = xx * (-1)*a.x + a.y;
	return;
}

double dis_between_line_and_point(pp a, pp b, pp c) {//点と直線の距離
	get_line(b, c);
	return abs(xx*a.x + yy * a.y + zz) / sqrt(xx*xx + yy * yy);
}

double ge(pp a, pp b) {
	return (b.y - a.y) / (b.x - a.x);
}


