#include <iostream>
#include <math.h>
#include <vector>

#define N 1000000
using namespace std;

int arr[N];

int main() {
	// 整数のふるいを作成する、初期設定
	int sievesize;
	std::cin >> sievesize;
	std::vector<int> sieve(sievesize, 1);

	// 1 ビットの位置を検索する
	for (int i = 2; i * i < sievesize; i++)
		if (sieve[i])
			for (int j = i + i; j < sievesize; j += i)
				sieve[j] = 0;

	// 最後に、設定される値を出力する
	std::cout << std::count(sieve.begin(),sieve.end(),0) << std::endl;
}
