#define MAX_E 1002
#define MAX_N 25100
#define MAX_M 20100
using namespace std;
typedef pair<int, int> P;
struct edge {
	int to;
	char cam;
	int cost;
};
int v;
vector<edge> G[MAX_N];
int d[MAX_N];
int n, m;

ll dijkstra(int s,bool ac,bool bc,bool cc,bool dc) {
	priority_queue<P, vector<P>, greater<P>>que;
	fill(d, d + n + 1, 2147483646);
	d[s] = 0;
	que.push(P(0, s));
	while (!que.empty()) {
		P p = que.top(); que.pop();
		int v = p.second;
		if (d[v] < p.first)continue;
		for (int i = 0; i < G[v].size(); i++) {
			edge e = G[v][i];
			if (e.cam == 'A' && !ac) {
				continue;
			}
			if (e.cam == 'B' && !bc) {
				continue;
			}
			if (e.cam == 'C' && !cc) {
				continue;
			}
			if (e.cam == 'D'&&!dc) {
				continue;
			}
			if (d[e.to] > d[v] + e.cost) {
				d[e.to] = d[v] + e.cost;
				que.push(P(d[e.to], e.to));
			}
		}
	}
	return d[n];
}
int main() {
	edge edg;
	ll jk = lolo;
	int h, k, l;
	int am, bm, cm, dm;
	char s;
	cin >> n >> m;
	cin >> am >> bm >> cm >> dm;
	rep(i, m) {
		cin >> h >> k >> s >> l;
		edg = { k,s,l };
		G[h].emplace_back(edg);
		edg = { h,s,l };
		G[k].emplace_back(edg);
	}
	rep(i, 2) {
		rep(j, 2) {
			rep(q, 2) {
				rep(o, 2) {
					jk = std::min(jk, (dijkstra(1, i, j, q, o) + (am*i + bm * j + cm * q + dm * o)));
					
				}
			}
		}
	}
	cout << jk << endl;
	return 0;
}
