#include <iostream>
#include <string>
#include <algorithm>
using namespace std;

int main()
{
	string s;
	cin >> s;
	cout << (count(s.begin(), s.end(), 'B') > count(s.begin(), s.end(), 'W') ? "Black wins" : count(s.begin(), s.end(), 'B') < count(s.begin(), s.end(), 'W') ? "White wins" : "Draw") << endl;
}
