string yn(bool b) { return (b ? "Yes" : "No"); }
string YN(bool b) { return (b ? "YES" : "NO"); }
struct BIT {
	int bit[500100];
	void reset(bool b = 0) {
		rep(i, 500100)bit[i] = (!b ? 0 : INF);
	}
	void add(ll n, ll p) {//v[p]+=n
		while (p < 500100) {
			bit[p] += n;
			p += p & -p;
		}
	}
	ll sum(ll p) {//v[1]~v[p]
		ll n = 0;
		while (p > 0) {
			n += bit[p];
			p -= p & -p;
		}
		return n;
	}
};
ll q, x;
BIT bit;
string s;
int main() {
	bit.reset();
	cin >> q;
	rep(i, q) {
		cin >> s >> x;
		if (s == "ADD")bit.add(1,x);
		else if (s == "DELETE") {
			ll l = 0, r = q, m;
			if (bit.sum(q)>=x) {
				while (1) {
					if (r - l < 2) {
						if (bit.sum(l) == x)m = l;
						else m = r;
						break;
					}
					m = (l + r) / 2;
					if (bit.sum(m) >= x)r = m;
					else l = m;
				}
				bit.add(-1, m);
			}
		}
		else {
			ll l = 0, r = q, m;
			if(bit.sum(q)<x)cout<<"Not Exist!"<<endl;
			else {
				while (1) {
					if (r - l < 2) {
						if (bit.sum(l) == x)m = l;
						else m = r;
						break;
					}
					m = (l + r) / 2;
					if (bit.sum(m) >= x)r = m;
					else l = m;
				}
				cout << m << endl;
			}
		}
	}
	return 0;
}
