#include<iostream>
#include<string>
using namespace std;
int main()
{
	string S;
	cin >> S;
	int B = 0, W = 0;
	for (int i = 0; i <= 99; i++) {
		if (S[i] = 'B') {
			B += 1;
		}
		else if (S[i] = 'W') {
			W += 1;
		}
	}
	if (B > W) {
		cout << "Black wins" << endl;
	}
	else if (B < W) {
		cout << "White wins" << endl;
	}
	else if (B == W) {
		cout << "Draw" << endl;
	}
	return 0;
}
