#include<iostream>
int main()
{
	int N, M;
	std::cin >> N >> M;
	int A[10000], B[10000];
	long double C[10000];
	int D = 0;
	for (int i = 0; i <= M - 1; i++) {
		std::cin >> A[i] >> B[i];
		C[i] = 0;
		C[i] = A[i] / B[i];
		if (i != 0) {
			if (C[i] < C[i - 1]) {
				D = i;
			}
		}
	}
	int ans = 0;
	int ANS;
	if (N%A[D] != 0) {
		ANS = N / A[D] + 1;
	}
	else {
		ANS = N / A[D];
	}
	std::cout << ANS * B[D] << std::endl;
	return 0;
}
