#include<iostream>
#include<string>
int main()
{
	std::string S;
	std::cin >> S;
	int B = 0, W = 0;
	for (int i = 0; i <= S.length(); i++) {
		if (S[i] == 'B') {
			B += 1;
		}
		else if (S[i] == 'W') {
			W += 1;
		}
	}
	if (B > W) {
		std::cout << "Black wins" << std::endl;
	}
	else if (B < W) {
		std::cout << "White wins" << std::endl;
	}
	else if (B == W) {
		std::cout << "Draw" << std::endl;
	}
	return 0;
}
