#include<iostream>
int main()
{
	int N, M;
	std::cin >> N >> M;
	int A[10000], B[10000];
	int C[10000];
	for (int i = 0; i <= M - 1; i++) {
		C[i] = 0;
		if (N%A[i] != 0) {
			C[i] = (N%A[i] + 1)*B[i];
		}
		else {
			C[i] = N % A[i] * B[i];
		}
	}
	int D = C[0];
	for (int i = 1; i = M - 1; i++) {
		if (D > C[i]) {
			D = C[i];
		}
	}
	std::cout << D << std::endl;
}
