# include <iostream>
# include <algorithm>
# include <vector>
# include <string>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")
using namespace std;
int main(void) {
	int n;
	string str;
	vector<char> firstChars = {}, lastChars = {};
	cin >> n;
	rep(i, n) {
		cin >> str;
		firstChars.push_back(str[0]);
		lastChars.push_back(str[str.size() - 1]);
	}
	sort(firstChars.begin(), firstChars.end());
	sort(lastChars.begin(), lastChars.end());

	Yes(firstChars == lastChars);
	return 0;
}
