#include<iostream>
#include<string>
int main() {
    std::string S;
    std::cin >> S;
    int B = 0;
       int W = 0;
    for (int i = 0; i <100; i++) {
        if (S[i] == 'B') {
            B++;
        }
        else if (S[i] == 'W') {
            W++;
        }
    }
    if (B > W) {
        std::cout << "Black wins" << std::endl;
    }
    else if (B < W) {
        std::cout << "White wins" << std::endl;
    }
    else if (B == W) {
        std::cout << "Draw" << std::endl;
    }
    
}
