#include<iostream>
#include<string>
int main() {
	int b=0, w=0;
	std::string s;
	std::cin >> s;
	for (int i = 0;i < 100;i++) {
		if (s[i] == 'B') {
			b = b + 1;
		}
		else if (s[i] == 'W') {
			w = w + 1;
		}
	}
	if (b > w) {
		std::cout << "Black wins" << std::endl;
	}
	else if (b < w) {
		std::cout << "White wins" << std::endl;
	}
	else if (b == w) {
		std::cout << "Draw" << std::endl;
	}
}
