#include<iostream>
#include<string>
int main() {
	int n, m, a[1000],b[10000],c=10000;
	std::cin >> n >> m;
	for (int i = 0;i < m;i++) {
		std::cin >> a[i];
		for (int j = 0;j < a[i];j++) {
			std::cin >>b[j];
			if (c > b[j]) {
				c = b[j];
			}
		}
	}
	std::cout << n * c << std::endl;
}
