#include<iostream>
#include<string>
int main()
{
	std::string str;
	std::getline(std::cin, str);
	do {
		for (int i = 0; i < str.length(); i++) {
			if (str[i] == 'z') {
				str[i] = 'a';
			}
			else if (str[i] < 'a' || 'z' < str[i]) {
				continue;
			}
			else {
				++str[i];
			}
		}
	} while (str.find("caesar") == std::string::npos);
	std::cout << str << std::endl;
}
