#include<iostream>
#include<string>
int main() {
	int n, m, a, b, c=10000;
	std::cin >> n >> m;
	for (int i = 0;i < m;i++) {
		std::cin >> a;
		if (n % a == 0) {
			std::cin >> b;
			if (c > b*n / a) {
				c = b * n / a;
			}
		}
		else {
			std::cin >> b;
			if (c > b*n / a + b) {
				c = b * n / a + b;
			}
		}
	}
	std::cout <<n * c << std::endl;
}
