#include <iostream>
#include <algorithm>

int main() {

	int N, M;
	
	std::cin >> N >> M;

	int min = 100000000; 
	for (int i = 0; i < M; ++i) {
		
		int A, B;

		std::cin >> A >> B;

		if (N % A == 0) {
			min = std::min(min, B * (N / A));
		}
		else {
			min = std::min(min, B * (N / A+1));
		}
	}

	std::cout << min << std::endl;

	return 0;
}
