#include <iostream>
#include <algorithm>

int main() {

    int N, M;
    
    std::cin >> N >> M;

    int min = 100000000;
    for (int i = 0; i < M; ++i) {
        
        int a, b;

        std::cin >> a >> b;

        if (N % a == 0) {
            min = std::min(min, b * (N / a));
        }
        else {
            min = std::min(min, b * (N / a + 1));
        }
    }

    std::cout << min << std::endl;

    return 0;
}
